package Demo;

public class switches {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char button='c';
		switch(button)
		{
		case 'a': System.out.println("red bulb on");
		break;
		case 'b': System.out.println("Yellow bulb on");
		break;
		case 'c': System.out.println("green bulb on");
		break;
		default: System.out.println("wrong button");
		break;
		
		}

	}

}
