package practice;

public class Days_Month {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int month=6;
		
		if(month==1) {
			System.out.println("31");
		}
		else if(month==2) {
			System.out.println("29/28");
		}
		else if(month==3) {
			System.out.println("31");
		}
		else if(month==4) {
			System.out.println("30");
		}
		else if(month==5) {
			System.out.println("31");
		}
		else if(month==6) {
			System.out.println("30");
		}
		else
			System.out.println("Invalid number");

	}

}
